<section class="container w1000 wrap-loaitin">    <div class="w1000">        <?php  $this->renderPartial("menuleft");?>        <div class="right page-cate">            <div class="w100">                <?php                $arrBread[0]["Name"] = $model->Name;                $criteria = new CDbCriteria();                $criteria->condition = "idNgonNgu = $this->lang and Active = 1";                $criteria->addInCondition("idLoaiTin",$this->arridloai);                $criteria->order = "t.id desc";                $criteria->with = "loaitin_lang";                $arrloai = Loaitin::model()->findAll($criteria);                $j = 0;                for ($i= (count($arrloai)-1); $i >= 0; $i--) {                    $j++;                    $arrBread[$j]["Href"] = "/loai-tin/".$arrloai[$i]->loaitin_lang->Alias.".html";                    $arrBread[$j]["Name"] = $arrloai[$i]->loaitin_lang->Name;                }                $this->renderPartial("//layouts/breadcrumb-news",array('data'=>$arrBread));?>                <!-- end breadbrum -->                <div class="line w100">                    <img class="w100" src="/images/line.jpg">                </div>            </div>            <div class="head w100"><?php echo $model->Name ?></div>            <div class="clear"></div>            <ul class="list-pro">                <?php                $i = 0;                foreach ($data as $key => $value) {                    $i++;                    $router = Router::model()->find("idObject = ". $value->tintuc_lang->id ." AND type = ".Router::TYPE_NEWS);                    ?>                    <li <?php echo ($i%3 == 0)? "sub":"" ?> >                        <a href="/<?php echo $router->alias ?>.html">                            <div class="wrap-img-cate">                                <img src="<?php echo $value->UrlImage ?>">                            </div>                            <div class="wrap-des-cate">                                <h3 ><?php echo $value->tintuc_lang->Name ?></h3>                                <div class="time-stamp">                                    <small><i>In</i> <?php echo $model->Name; ?></small> &nbsp;                                    <small class="date"><i class="fa fa-clock-o"></i> <?php echo date("d/m/Y",$value->Date) ?></small>                                </div>                                <p class="w100">                                    <?php                                    if($value->tintuc_lang->Description != "" && strlen($value->tintuc_lang->Description) > 400)                                        $mota = $value->tintuc_lang->Description;                                    else                                        $mota = $value->tintuc_lang->Content;                                    echo Common::getDescription($mota,400); ?>                                </p>                            </div>                        </a>                    </li>                <?php } ?>            </ul>            <div class="clear"></div>            <?php $this->renderPartial("//layouts/pagination",array("pages"=>$pages));?>        </div>    </div>    </div></section>