<main>    <div class="hd-container">        <div class="mainpage ">            <?php            $arrBread[0]["Name"] = $model->Name;            $criteria = new CDbCriteria();            $criteria->with = "loaisanpham_lang";            $criteria->condition = "idNgonNgu = $this->lang and Active = 1";            $criteria->order = "t.id desc";            $criteria->addInCondition("idLoai",$this->arridloai);            $arrloai = Loaisanpham::model()->findAll($criteria);            $j = 0;            for ($i= (count($arrloai)-1); $i >= 0; $i--) {                $j++;                $arrBread[$j]["Name"] = $arrloai[$i]->loaisanpham_lang->Name;                $routerCateNews = Router::model()->find("idObject = " . $arrloai[$i]->loaisanpham_lang->id . " AND type = " . Router::TYPE_NEWS_PRODUCT);                $arrBread[$j]["Href"] = $routerCateNews->alias;            }            $this->renderPartial("//layouts/breadcrumb",array('data'=>$arrBread));            ?>            <div class="cate1">                <div class="hd-row">                    <?php                    foreach ($data as $item) {                        $router = Router::model()->find("idObject = ". $item->tintuc_lang->id ." AND type = ".Router::TYPE_NEWS);                    ?>                    <figure>                        <img src="<?php echo $item->UrlImage ?>" class="aligncenter">                        <figcaption>                            <a class="tt" href="<?php echo $router->alias ?>.html">                                <?php echo $item->tintuc_lang->Name ?>                            </a>                           <?php echo Common::getDescription($item->tintuc_lang->Description,500).'...'; ?>                            <div class="btn-lk">                                <a class="hd-btn" href="<?php echo $router->alias ?>.html">Đọc tiếp <i class="icon-right-3"></i></a>                            </div>                        </figcaption>                    </figure>                    <?php } ?>                </div>            </div>            <div class="page_nav">                <div class="wp-pagenavi">                    <?php $this->renderPartial("//layouts/pagination",array("pages"=>$pages));?>                </div>            </div>        </div>        <?php $this->renderPartial('sidebar'); ?>    </div></main>   