<?php/** * @var $title * @var $id * @var $idParent */?><div class="right menuright">    <div class="wrap-category">        <label>            <?php echo $title ?>        </label>        <div class="content w100">            <ul>                <?php                $idParent = (int)$idParent;                if ($idParent == 0) {                    $idParent = $id;                }                $criteria = new CDbCriteria();                $criteria->with ="loaisanpham_lang";                $criteria->condition = "t.Parent = ".$idParent;                $data = Loaisanpham::model()->findAll($criteria);                foreach ($data as $item) {                    $router = Router::model()->find("idObject = " . $item->loaisanpham_lang->id . " AND type = " . Router::TYPE_PRODUCT_CATEGORY);                    $selected = $item == $id ? 'active' : '';                ?>                <li class="<?php echo $selected ?>">                    <a herf="/<?php echo $router->alias ?>.html">                        <?php echo $item->loaisanpham_lang->Name ?> aa                    </a>                </li>                <?php } ?>            </ul>        </div>    </div></div>