<link rel="stylesheet" type="text/css" href="/css/carousel/owl.carousel.css"><script type="text/javascript" src="/js/carousel/owl.carousel.min.js"></script><div class="slide Carousel">    <?php    $slide = Hinhanh::getDataByCustomSetting('slide');    foreach ($slide->hinhanh as $value){        ?>        <div>            <img src="<?php echo $value->url_image ?>">        </div>    <?php } ?></div><script>    $(document).ready(function(){        $(".slide").owlCarousel({            autoplay:true,            smartSpeed : 900,            navigation : true, // Show next and prev buttons            paginationSpeed : 9000,            loop:true,            items:1        });    });</script>